class BouncingText {
  constructor(letter, x, y, xv, yv) {
    this.letter = letter;
    this.x = x;
    this.y = y;
    this.xv = xv;
    this.yv = yv;
  }

  drawText() {
    text(this.letter, this.x, this.y);
  }

  bounce() {
    this.x += this.xv;
    this.y += this.yv;
    if (this.x < 0 || this.x > width) {
      this.xv *= -1;
    }
    if (this.y < 0 || this.y > height) {
      this.yv *= -1;
    }
  }

  reverseTime() {
    if (frameCount % int(frameRate()) == 0) {
      this.xv *= -1;
      this.yv *= -1;
    }
  }
}

