    let { x, y } = faceMesh_inputPtToOutput(keypoint);
    fill(0, 255, 0);
    noStroke();
    circle(x, y, 2);
  }
}

function draw_face_mesh(face) {
  faceMesh_render(my, face.keypoints);
}

