function transInversePt(pt) {
  let { x, y } = pt;
  // x = (x - my.x0k) * my.rx + my.x0;
  // y = (y - my.y0k) * my.ry + my.y0;
  x = my.x0k + (x - my.x0) / my.rx;
  y = my.y0k + (y - my.y0) / my.ry;
  return { x, y };
}

