  my.xspeed = 1;
  my.yspeed = 1;
  // my.xlen = 0;
  // my.ylen = 0;
  my.debug = 1;
  // my.fullScreenBtn = 0;
  my.startTime = 0;
  my.changeTime = 1.0;

  if (!my.debug) {
    my.width = windowWidth;
    my.height = windowHeight;
  }
  createCanvas(my.width, my.height);
  noStroke();

  my.fullScreenBtn = createButton("Full Screen");
  my.fullScreenBtn.mousePressed(full_screen_action);
  my.fullScreenBtn.style("font-size:42px");

  my.startTime = millis() / 1000.0;

  new_pos();

  // init_dim();
}

function draw() {
  background(0);
  // strokeWeight(0);
  // fill("black");
  // rect(0, 0, width, height);
  check_time();

  strokeWeight(1);
  stroke(255);

  line(0, my.ypos, width, my.ypos);

  line(my.xpos, 0, my.xpos, height);

  my.ypos += my.yspeed;
  if (my.ypos > height || my.ypos < 0) {
    my.yspeed *= -1;
  }
  my.xpos += my.xspeed;
  if (my.xpos > width || my.xpos < 0) {
    my.xspeed *= -1;
  }
}

function new_pos() {
  my.xpos = random(0, width);
  my.ypos = random(0, height);
}

function check_time() {
  let now = millis() / 1000;
  if (now - my.startTime > my.changeTime) {
    my.startTime = now;
    new_pos();
  }
}

