function setup() {
  my_setup();
  my_resize();

  createCanvas(my.width, my.height);

  my.capture = createCapture(VIDEO);
  my.capture.size(width, height);
  my.capture.hide();
}

function draw() {
  strokeWeight(my.strokeWeight);
  my.img = my.capture.get();
  let more = 1;
  while (more) {
    more = draw_out();
    if (!my.faster) more = 0;
  }
}

function my_setup() {
  my.faster = 1;

  my.strokeWeight = 10;
  my.rim = 5;
  my.angle = 0;
  my.angleStep = 1;

  my.xstep = 1;
  my.xstepDir = 1;
  my.xstepDownFactor = 4;
  my.xposStart = 0;
  my.xpos = my.xposStart;

  my.secsPerUpdate = 0.1;
  // my.secsPerUpdate = 0.01;
  my.secsDelta = 0;

  // my.x0;
  // my.y0;
  // my.capture;
  // my.img;
}

function my_resize() {
  my.width = windowWidth;
  my.height = windowHeight;
  my.x0 = int(my.width / 2);
  my.y0 = int(my.height / 2);
  my.xposEnd = my.height;
}

function draw_out() {
  // colorMode(HSB);

  let r = my.xpos / 2;
  let rang = radians(my.angle);
  let x1 = r * cos(rang);
  let y1 = r * sin(rang);

  let c1 = my.img.get(my.x0 + x1, my.y0 + y1);
  stroke(c1);
  fill(c1);
  circle(my.x0 + x1, my.y0 + y1, my.rim);

  let r2 = my.width;
  let x2 = r2 * cos(rang);
  let y2 = r2 * sin(rang);
  line(my.x0 + x1, my.y0 + y1, my.x0 + x2, my.y0 + y2);

  my.angle = my.angle + my.angleStep;
  if (my.angle > 360) {
    my.angle = 0;
    next_step();
    return 0;
  }
  return 1;
}

function next_step() {
  my.secsDelta += deltaTime / 1000;
  if (my.secsDelta < my.secsPerUpdate) {
    return;
  }
  my.secsDelta = 0;
  my.xpos += my.xstep;
  if (my.xstep > 0 && my.xpos > my.xposEnd) {
    my.xstepDir *= -1;
    my.xstep = my.xstepDir * my.xstepDownFactor;
    my.xpos += my.xstep;
  } else if (my.xstep < 0 && my.xpos < my.xposStart) {
    my.xstepDir *= -1;
    my.xstep = my.xstepDir;
    my.xpos += my.xstep;
  }
}

function windowResized() {
  resizeCanvas(windowWidth, windowHeight);
  my_resize();
}

