  let captionSize = height / my.captionScale;
  layer.textSize(captionSize);
  let tw = layer.textWidth(str);
  let th = layer.textLeading();
  let ta = layer.textAscent();
  // Draw center top
  // let x = 0;
  let x = width / 2 - tw / 2;
  // let y = height - th;
  let y = 0;
  layer.strokeWeight(0);
  if (my.record) {
    layer.fill(255, 0, 0);
  } else {
    layer.fill(0);
  }
  layer.rect(x, y, tw, th);
  layer.fill(255);
  layer.text(str, x, y + ta);
}

