//
// Draw panes in my.panes
//
function draw_panes() {
  let status = {};
  for (let uid in my.panes) {
    let pane = my.panes[uid];
    // console.log('draw_panes pane', pane);
    pane.prepare_layer(status);
    {
      // image(pane.layer, pane.layout.x0, pane.layout.y0);
      let img = pane.layer;
      let dx = pane.layout.x0;
      let dy = pane.layout.y0;
      let dWidth = my.xlen;
      let dHeight = my.ylen;
      let sx = 0;
      let sy = 0;
      if (img) {
        image(img, dx, dy, dWidth, dHeight, sx, sy, img.width, img.height);
      } else {
        console.log('draw_panes img', img, 'pane', pane);
      }
    }
  }
  if (status.cleared) {
    background(0);
  }
}

function deinit_panes() {
  for (let uid in my.panes) {
    let pane = my.panes[uid];
    pane.deinit();
  }
  my.panes = {};
  my.last_ndevices = 0;
}

function build_panes() {
  let layouts = layout_devices();
  my.panes = {};
  for (let layout of layouts) {
    // console.log('build_panes layout', layout);

    // layout = { x0: x, y0: y, uid, width, height, device }
    let { width, height, uid } = layout;
    let pane = new Pane({ width, height, uid, layout });
    pane.sync(layout.device);

    my.panes[layout.uid] = pane;
  }
}

function layout_needed_check() {
  let a_devices = dbase_a_devices();
  let ndevices = a_devices.length;
  if (ndevices != my.last_ndevices) {
    console.log('layout_needed_check new ndevices', ndevices);
    my.last_ndevices = ndevices;
    return 1;
  }
  return 0;
}

// returns: [ { device, x0: x, y0: y }, ... ]
//
function layout_devices() {
  my.ndiv = 1;
  let a_devices = dbase_a_devices();
  let ndevices = a_devices.length;
  let more;
  let layouts;
  do {
    more = 0;
    // my.xlen = width / my.ndiv;
    // my.ylen = my.xlen * (16 / 9);
    my.ylen = height / my.ndiv;
    my.xlen = my.ylen * (9 / 16);
    let xhalf = my.xlen * 0.5;
    let yhalf = my.ylen * 0.5;
    let x0 = 0;
    let x = x0;
    let y = 0;
    layouts = [];
    for (let index = 0; index < ndevices; index++) {
      let device = a_devices[index];
      if (!device) {
        console.log('layout_devices no device', index, device);
        return;
      }
      {
        let { uid, width, height } = device;
        if (width != undefined) {
          layouts.push({ x0: x, y0: y, uid, width, height, device });
        }
      }
      // console.log('layout_devices x', x, 'y', y, 'uid', device.uid);
      if (index != ndevices - 1) {
        x += my.xlen;
      }
      if (x + xhalf > width) {
        x = x0;
        y += my.ylen;
        if (y + yhalf > height) {
          my.ndiv += 1;
          more = 1;
          // console.log('layout_devices more', more, 'index', index);
          break;
        }
      }
    }
    // console.log('layout_devices more', more);
  } while (more);
  return layouts;
}

//
class Pane {
  //
  // { width, height, db_update, cross_limit, isRemote }
  // { width, height, uid, layout }
  //
  constructor(props) {
    //
    let my = this;

    Object.assign(my, props);

    my.layer = createGraphics(my.width, my.height);

    my.brush_color_index = 0;
    my.cross_color_index = 0;
    my.hitEdge = 0;
    my.brush_size = 4;
    my.cross_size = 4;
    my.await_sync = my.db_update;
    my.init_xy(0);

    if (my.db_update) {
      let { width, height } = my;
      dbase_queue_update({ width, height });
    }
  }

  deinit() {
    let my = this;
    if (my.layer) {
      my.layer.remove();
    }
  }

  prepare_layer(status) {
    let my = this;
    my.trackBrush();
    my.render(status);
  }

  sync(device) {
    let my = this;
    my.await_sync = 0;
    Object.assign(my, device);
  }

  init_xy(db_update) {
    let my = this;
    let x = my.width * 0.5;
    let y = my.height * 0.5;
    my.init_brush(x, y, db_update);
    my.init_cross(x, y, db_update);
  }

  init_brush(x, y, db_update) {
    let my = this;
    my.brush_x0 = x;
    my.brush_y0 = y;
    if (db_update) {
      let { brush_x0, brush_y0 } = my;
      dbase_queue_update({ brush_x0, brush_y0 });
    }
  }

  init_cross(x, y, db_update) {
    let my = this;
    my.cross_x0 = x;
    my.cross_y0 = y;
    my.xLeft = my.cross_x0;
    my.yTop = my.cross_y0;
    my.xRight = my.cross_x0;
    my.yBottom = my.cross_y0;
    if (db_update) {
      let { cross_x0, cross_y0, xLeft, yTop, xRight, yBottom } = my;
      dbase_queue_update({ cross_x0, cross_y0, xLeft, yTop, xRight, yBottom });
    }
  }

  render(status) {
    let my = this;
    if (my.await_sync) return;
    if (!status) status = {};
    if (dbase_actions_issued(my.uid, { clear_action: 1 })) {
      // console.log('Pane render_cross uid', my.uid, 'clear_action', my.clear_action);
      status.cleared = 1;
      if (my.isRemote) {
        background(0);
      }
      my.layer.clear();
    }
    my.render_cross();
  }

  mouseDragged() {
    let my = this;
    let colr = Pane.colors[my.brush_color_index];
    my.layer.strokeWeight(my.brush_size);
    my.layer.stroke(colr);
    my.layer.line(pmouseX, pmouseY, mouseX, mouseY);
    if (my.db_update) {
      my.brush_x0 = pmouseX;
      my.brush_y0 = pmouseY;
      my.brush_x1 = mouseX;
      my.brush_y1 = mouseY;
      let { brush_x0, brush_y0, brush_x1, brush_y1 } = my;
      dbase_queue_update({ brush_x0, brush_y0, brush_x1, brush_y1 });
    }
  }

  trackBrush() {
    let my = this;
    let colr = Pane.colors[my.brush_color_index];
    my.layer.strokeWeight(my.brush_size);
    my.layer.stroke(colr);
    if (my.brush_px1 != undefined) {
      if (my.brush_px1 > -1 && my.brush_x1 > -1) {
        my.layer.line(my.brush_px1, my.brush_py1, my.brush_x1, my.brush_y1);
      }
    }
    my.brush_px1 = my.brush_x1;
    my.brush_py1 = my.brush_y1;
  }

  mousePressed() {
    let my = this;
    my.lastMouseX = mouseX;
    my.lastMouseY = mouseY;
  }

  mouseReleased() {
    let my = this;
    if (mouseX == my.lastMouseX && mouseY == my.lastMouseY) {
      my.init_cross(mouseX, mouseY, my.db_update);
      my.next_crossColor();
    } else {
      my.next_brushColor();
    }
    if (my.db_update) {
      let brush_x1 = -1;
      dbase_queue_update({ brush_x1 });
    }
  }

  next_crossColor() {
    let my = this;
    my.cross_color_index = (my.cross_color_index + 1) % Pane.colors.length;
    if (my.db_update) {
      let cross_color_index = my.cross_color_index;
      dbase_queue_update({ cross_color_index });
    }
  }

  next_brushColor() {
    let my = this;
    my.brush_color_index = (my.brush_color_index + 1) % Pane.colors.length;
    if (my.db_update) {
      let brush_color_index = my.brush_color_index;
      dbase_queue_update({ brush_color_index });
    }
  }

  crossColor() {
    let my = this;
    return Pane.colors[my.cross_color_index];
  }

  adjust_brush_size(delta) {
    let my = this;
    if (delta > 0 || my.brush_size > 1) {
      my.brush_size += delta;
    }
    if (my.db_update) {
      let { brush_size } = my;
      dbase_queue_update({ brush_size });
    }
  }

  adjust_cross_size(delta) {
    let my = this;
    if (delta > 0 || my.cross_size > 1) {
      my.cross_size += delta;
    }
    if (my.db_update) {
      let { cross_size } = my;
      dbase_queue_update({ cross_size });
    }
  }

  render_cross() {
    let my = this;

    my.xRight += 1;
    if (my.xRight > my.width * my.cross_limit + my.cross_x0) {
      my.hitEdge += 1;
      my.xRight = my.cross_x0;
    }
    my.layer.strokeWeight(my.cross_size);
    my.layer.stroke(my.crossColor(0));
    my.layer.line(my.cross_x0, my.cross_y0, my.xRight, my.cross_y0);

    my.yBottom += 1;
    if (my.yBottom > my.height * my.cross_limit + my.cross_y0) {
      my.hitEdge += 1;
      my.yBottom = my.cross_y0;
    }
    my.layer.stroke(my.crossColor(1));
    my.layer.line(my.cross_x0, my.cross_y0, my.cross_x0, my.yBottom);

    my.xLeft -= 1;
    if (my.xLeft < my.cross_x0 - my.width * my.cross_limit) {
      my.hitEdge += 1;
      my.xLeft = my.cross_x0;
    }
    my.layer.stroke(my.crossColor(2));
    my.layer.line(my.cross_x0, my.cross_y0, my.xLeft, my.cross_y0);

    my.yTop -= 1;
    if (my.yTop < my.cross_y0 - my.height * my.cross_limit) {
      my.hitEdge += 1;
      my.yTop = my.cross_y0;
    }
    my.layer.stroke(my.crossColor(3));
    my.layer.line(my.cross_x0, my.cross_y0, my.cross_x0, my.yTop);

    if (my.hitEdge >= 4) {
      my.hitEdge = 0;
      my.next_crossColor();
    }

    if (my.db_update) {
      let { xLeft, yTop, xRight, yBottom } = my;
      dbase_queue_update({ xLeft, yTop, xRight, yBottom });
    }
  } // render_cross

  //
  static colorGold = [187, 165, 61];
  // static colors = ['red', 'green', Pane.colorGold, 0];
  // static colors = [[255, 0, 0], [0, 255, 0], Pane.colorGold, 0];
  static colors = [[255, 0, 0], [0, 255, 0], Pane.colorGold];
}

