async function take_action() {
  // console.log('take_action');

  let entry = photo_list_entry(my.photo_index + 1);
  let path = photo_path_entry(entry);

  let layer = my.canvas;
  let imageQuality = my.imageQuality;
  try {
    await fstorage_upload({ path, layer, imageQuality });

    await photo_list_add(entry);

    dbase_group_update({ photo_index: dbase_increment(1) });
    //
  } catch (err) {
    console.log('take_action err', err);
  }
}

async function remove_action() {
  // console.log('remove_action photo_count', my.photo_list.length);
  if (my.photo_list.length < 1) {
    // No more images in the cloud
    //  zero out photo_index
    dbase_group_update({ photo_index: 0 });
    return;
  }
  //
  // remove the last entry in photo_list
  //
  let last = my.photo_list.pop();
  await photo_list_remove_entry(last);

  // Update photo_list in the cloud
  dbase_group_update({ photo_list: my.photo_list });
}

// incrementally draw grid of pixel rects from given image img
function pix_grid_update(img) {
  // console.log('pix_grid_update img', img);
  if (!img) return;
  // console.log('pix_grid_update_image img', img);
  more = 1;
  let colr;
  let cx = floor(my.stepPx * 0.5);
  let cy = floor(my.stepPx * 0.5);
  while (more) {
    let vx = my.send_xi * my.stepPx;
    let vy = my.send_yi * my.stepPx;
    if (my.videoFlag) {
      colr = img.get(vx + cx, vy + cy);
    } else {
      colr = [0, 0, 0];
    }
    draw_record_rect(my.send_xi, my.send_yi, colr);
    my.send_xi += 1;
    vx = my.send_xi * my.stepPx;
    if (vx >= my.vwidth) {
      draw_record_flush(my.send_yi);
      my.send_xi = 0;
      my.send_yi += 1;
      vy = my.send_yi * my.stepPx;
      if (vy >= my.vheight) {
        more = 0;
        my.send_yi = 0;
      }
      if (my.byLine) {
        more = 0;
      }
    }
    if (my.byPixel) {
      more = 0;
    }
  }
}

function draw_record_rect(ix, iy, c) {
  // console.log('draw_record_rect ix', ix, 'iy', iy, 'c', c);
  if (my.storeFlag) {
    // let op = { r: 1, c, x, y, w, h };
    if (!my.pixRows) {
      my.pixRows = [];
    }
    let row = my.pixRows[iy];
    if (!row) {
      row = [];
      my.pixRows[iy] = row;
    }
    let item = { c };
    row[ix] = item;
  }
}

function draw_record_flush(irow) {
  if (my.storeFlag && irow >= 0) {
    pix_grid_update_irow(irow, my.stepPx, my.pixRows[irow]);
  }
}

