let my = {};

// mo-vote/device/{uid}/vote
//    individual vote

function setup() {
  my_setup(); // setup firebase configuration

  my.canvas = createCanvas(my.width, my.height);
  // noCanvas();

  dbase_app_init({ completed: startup_completed }); // callback function when app init

