function make_earth() {
  let img = my.earthImg;
  let uu = floor(my.height * uuScale);
  let x = my.width - uu;
  let y = 0;
  let width = uu;
  let height = uu;
  let orbitControl = 1;
  my.earth = new HeavenlyBody({ x, y, width, height, img, orbitControl });
  my.earth.label = 'earth';
  my.earth.setDir(dirRight);
