function make_moon() {
  let img = my.moonImg;
  let uu = floor(my.height * uuScale);
  let x = 0;
  let y = 0;
  let width = uu;
  let height = uu;
  let orbitControl = 1;
  my.body2 = new HeavenlyBody({ x, y, width, height, img, orbitControl });
