  let img = null;
  let width = uu;
  let height = uu;
  let orbitControl = 1;
  let capture = my.capture;
  my.camBody = new HeavenlyBody({ x, y, width, height, img, orbitControl, capture });
  my.camBody.label = 'camBody';
  my.camBody.setDir(dirStop);
  // Spiral from top of camera
  my.camBody.setAngle(-1.5, 3.0, 0);
}

