  scrolling: 1,
  nstep: 64,
  margin: 0.1,
};

function setup() {
  my_init();

  // createCanvas(my.width, my.height);
  createCanvas(windowWidth, windowHeight-40);
  background(255);
  noStroke();

  create_myVideo();

  create_ui();
}

function draw() {
  if (!video_ready()) return;

  check_scroll();

  // faster to get entire video frame as an image
  let img = my.video.get();
  let stepPx = my.stepPx;
  let rr = stepPx * (1 - my.margin);
  let vx = 0;
  let vy = 0;

  image(img, vx, vy);

  while (vy < my.vheight) {
    let col = img.get(vx, vy);
    fill(col);
    rect(vx, vy, rr, rr);
    vx += stepPx;
    if (vx > my.vwidth) {
      vx = 0;
      vy += stepPx;
    }
  }
}

