class Paddle {
  constructor() {
    this.y = height - 20;
    this.w = 100;
    this.h = 20;
    this.xchange = 0;
    this.x = (width - this.w) / 2;
  }
  show() {
    fill(255);
    rectMode(CENTER);
    rect(this.x, this.y, this.w, this.h);
  }
  update() {
    this.x += this.xchange;
    // this.x = constrain(this.x, this.w, width - this.w);
    this.x = constrain(this.x, 0, width);
  }
  move(steps) {
    this.xchange = steps;
  }
}

class Puck {
  constructor() {
    this.x = width / 2;
    this.y = 0;
    this.xspeed = 0;
    this.yspeed = 0;
    this.r = 12;
    this.reset();
  }
  show() {
    fill(255);
    ellipse(this.x, this.y, this.r * 2);
  }
  checkPaddle(p) {
    if (
      this.y - this.r < p.y + p.h / 2 &&
      this.y + this.r > p.y - p.h / 2 &&
      this.x - this.r < p.x + p.w / 2 &&
      this.x + this.r > p.x - p.w / 2
    ) {
      // let diff = this.y - (p.y - p.h / 2);
      // let rad = radians(45);
      // let angle = map(diff, 0, p.h, -rad, rad);
      // this.xspeed = 5 * cos(angle);
      // this.yspeed = 5 * sin(angle);
      this.yspeed *= -1;
      this.xspeed *= -1;
      my.score++;
      my.ding.play();
    }
  }
  update() {
    this.x += this.xspeed;
    this.y += this.yspeed;
  }
  reset() {
    this.x = width / 2;
    this.y = 0;
    // let angle = random(radians(45), radians(90+45));
    let angle = random(my.angles)
    this.xspeed = 5 * Math.cos(angle);
    // this.xspeed = 0;
    this.yspeed = 5 * Math.sin(angle);
    if (random(1) < 0.5) {
      this.xspeed *= -1;
    }
  }
  edges() {
    if (this.x < 0 || this.x > width) {
      this.xspeed *= -1;
    }
    if (this.y < 0) {
      this.yspeed *= -1;
    }
    if (this.y - this.r > height) {
      my.score--;
      this.reset();
    }
  }
}

