function check_intersects() {
  for (let i = 0; i < items.length; i++) {
    let b1 = items[i];
    for (let j = i + 1; j < items.length; j++) {
      let b2 = items[j];
      if (b1.intersects(b2)) {
        // console.log('interects i', i, 'j', j)
        // stop this ball from moving since it touches another
        b2.speedY = 0;
        b1.speedY = 0;
      }
    }
  }
