  my.width = my.vwidth;
  my.height = my.vheight;

  my.x = floor(my.width / 2);
  my.y = floor(my.height / 2);
  my.px = my.x;
  my.py = my.y;

  my.layer = createGraphics(my.width, my.height);

  createCanvas(my.width, my.height);
  background(255);
  noStroke();

  create_myVideo();

