function mouseDragged() {
  my.layer.strokeWeight(my.brushSize);
  let col = my.video.get(mouseX, mouseY);
  my.layer.stroke(col);
  my.layer.line(mouseX, mouseY, pmouseX, pmouseY);

  my.x = mouseX
  my.y = mouseY

  // Prevent canvas drag on mobile devices
  return false;
}

function draw_jittler() {
  let bz = my.brushSize
  my.layer.strokeWeight(bz);
  let { x, y, px, py } = my;
  let col = my.video.get(x, y);
  my.layer.stroke(col);
  my.layer.line(x, y, px, py);
  my.px = x;
  my.py = y;
  my.x += random(-bz,bz);
  my.y += random(-bz,bz);
  my.x = (my.x + my.width) % my.width;
  my.y = (my.y + my.height) % my.height;
}

