  }

  //Level 2
  if(level == 2) {
    win(width - 50, (height - 150)/2, 60);
  }

  //Level 3
  if(level == 3) {
    win(300, 100, 60);
  }

  if(level % 1 == 0) {
    //player
    pcolor = color(255, 0, 0, alpha);
    drawPlayer();
  }
}

//advance levels
