  rect(x - s/2, y - s/2, s, s);
  if(px > x - s/2 && px < x + s/2 - psize && py > y - s/2 && py < y + s/2 - psize) {
    immune = true;
  }
  if(immune) {
    alpha -= 8.5;
    if(alpha <= 0) {
      resetWalls();
