let nwidth = 640;
let nheight = 480;
let sw = 10;
let n = 20;
let nfrom = 20;
let nto = 1;
let rcenter = 20;
let x0;
let y0;
let capture;
let ang = 0;
let astep = 1;
let xgap = nwidth / n;
let ygap = nheight / n;
let xstep = nwidth / n;
let ystep = nheight / n;

function setup() {
  createCanvas(nwidth, nheight);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide();
  x0 = int(nwidth / 2);
  y0 = int(nheight / 2);
}

function draw() {
  draw_out();
}

function draw_out() {
  // colorMode(HSB);
  strokeWeight(sw);
  // background(255);

  // image(img, dx, dy, dWidth, dHeight, sx, sy, [sWidth], [sHeight],
  let img = capture.get();
  // image(img, 0, 0, nwidth);

  let r = xgap / 2;
  let rang = radians(ang);
  let x1 = r * cos(rang);
  let y1 = r * sin(rang);

  let c1 = img.get(x0 + x1, y0 + y1);
