  line(x0 + x1, y0 + y1, x0 + x2, y0 + y2);

  ang = ang + astep;
  if (ang > 360) {
    ang = 0;
    next_n();
  }
}

function next_n() {
  n--;
  if (n < nto) {
    n = nfrom;
  }
  xgap = nwidth / n;
  ygap = nheight / n;
  xstep = nwidth / n;
  ystep = nheight / n;
