let astep = 1;
let faster = 1;
let img;

let n = 20;
let nfrom = 20;
let nto = 1;
let xgap_start = rcenter;
let xgap_end = nheight;
let xgap = xgap_start;
let xstep = rcenter;
let outter_radius = nwidth;
let secsPerUpdate = 0.1;
let secsDelta = 0;

function setup() {
  createCanvas(nwidth, nheight);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide();
  x0 = int(nwidth / 2);
  y0 = int(nheight / 2);
}

function draw() {
  strokeWeight(sw);
  img = capture.get();
  let more = 1;
  while (more) {
    more = draw_out();
    if (! faster) more = 0;
  }
}

function draw_out() {
  // colorMode(HSB);

  let r = xgap / 2;
  let rang = radians(ang);
  let x1 = r * cos(rang);
  let y1 = r * sin(rang);

  let c1 = img.get(x0 + x1, y0 + y1);
  stroke(c1);
  fill(c1);
  circle(x0 + x1, y0 + y1, rcenter);

  r = outter_radius;
  if (xgap + xstep > xgap_end) {
    r = nwidth;
  }
  if (r > 0) {
    let x2 = r * cos(rang);
    let y2 = r * sin(rang);
    line(x0 + x1, y0 + y1, x0 + x2, y0 + y2);
  }

  ang = ang + astep;
  if (ang > 360) {
    ang = 0;
    next_step();
    return 0;
  }
  return 1;
}

function next_step() {
  secsDelta += deltaTime / 1000;
  if (secsDelta < secsPerUpdate) {
    return;
  }
  secsDelta = 0;
  xgap += xstep;
  if (xgap > xgap_end) {
    xgap = xgap_start;
    if (capture.loadedmetadata) {
      outter_radius = -1;
    }
  }
}

