function scan_x0y0() {
  x0 += xstep;
  if (x0 > width) {
    x0 = 0;
    y0 += ystep;
    if (y0 > height) {
      y0 = 0;
    }
  }
}

function draw_gap() {
  let img = capture.get();
  let y1 = y0 - ygap / 2;
  let y2 = y0 + ygap / 2;
  let x1 = x0 - xgap / 2;
  let x2 = x0 + xgap / 2;
  for (let x = 0; x < img.width; x++) {
    let c1 = img.get(x, y1);
    stroke(c1);
    line(x, 0, x, y1);
    let c2 = img.get(x, y2);
    stroke(c2);
    line(x, y2, x, nheight);
  }
  for (let y = y1; y < y2; y++) {
    let c1 = img.get(x1, y);
    stroke(c1);
    line(0, y, x1, y);
    let c2 = img.get(x2, y);
    stroke(c2);
    line(x2, y, nwidth, y);
  }
}

