  createCanvas(cat.width, cat.height);
  noStroke();
}

function draw() {
  background(220);

  let img = cat.get();
  for(let x = 0; x < cat.width; x+=cellSz) {
    for(let y = 0; y < cat.height; y+=cellSz) {

      let c = img.get(x, y);

      fill(c);

      rect(x, y, cellSz, cellSz);
    }
  }
  cellSz = floor(map(mouseX, 0, width, 1, 10));
}

