let faces = [];

function setup() {
  createCanvas(400, 400);
  // randomSeed(0);
  for (let i = 0; i < 3; i++) {
    faces.push(new Face());
  }
}
function draw() {
  background(220);
  // circle(xp, yp, 20);
  for (let i = 0; i < faces.length; i++) {
    let face = faces[i];
    face.drawFace();
    face.bounce();
  }
