class Face {
  constructor() {
    this.x = width / 2;
    this.y = height / 2;
    this.xs = random(-5, 5);
    this.ys = random(-5, 5);
    this.c = random(["red", "green", "gold"]);
  }
  drawFace() {
    // background(220);
    push();
    noFill();
    stroke(this.c);
    strokeWeight(8);
    translate(this.x - 200, this.y - 200);
    this.drawHead();
    this.drawEyes();
    this.drawMouth();
    pop();
  }
  bounce() {
    this.x += this.xs;
    this.y += this.ys;
    if (this.x < 0 || this.x > width) {
      this.xs *= -1;
    }
    if (this.y < 0 || this.y > height) {
      this.ys *= -1;
    }
  }
  drawHead() {
    circle(200, 200, 300);
  }
  drawEyes() {
    ellipse(150, 150, 40, 100);
    ellipse(250, 150, 40, 100);
  }
  drawMouth() {
    arc(200, 225, 200, 160, 0, radians(180), CHORD);
  }
}

