    a_len = this.value();
  });
  createSpan().id('ia_len');
  createElement('br');
  createSpan().id('icircleX');
  createElement('br');
  // createCheckbox([label], [value])
  createCheckbox('Move', a_move).changed(function() {
    a_move = this.checked();
  });
}

