    noStroke();
    fill(this.color);
    circle(this.x, this.y, this.s);
    this.x = this.x + this.xdir;
    this.y = this.y + this.ydir;
    if (this.y >= height || this.y <= 0) {
      this.bounceY();
    }
    if (this.x >= width || this.x <= 0) {
      this.bounceX();
    }
  }
  intersects(jb) {
    return (
      this.x >= jb.x && //
      this.x <= jb.x + jb.s &&
      this.y >= jb.y &&
      this.y <= jb.y + jb.s
    );
  }
  bounceX() {
    this.xdir = this.xdir * -1;
    playNote(this.note, 1000);
  }
  bounceY() {
    playNote(this.note, 1000);
    this.ydir = this.ydir * -1;
  }
}

