function playNote(note, duration) {
  // Make phone vibrate
  navigator.vibrate(note);
  // console.log("playNote", note, "duration", duration);
  // osc.freq(midiToFreq(note));
  // osc.freq(220);
  osc.freq(note);
  // Fade it in
  osc.fade(0.5, 0.2);

  // If we sest a duration, fade it out
  if (duration) {
    setTimeout(function () {
      osc.fade(0, 0.2);
    }, duration - 50);
  }
}

