  tint(255,my.alpha)

  if (my.scanX) {
    scan_x();
  }  
  else {
    scan_y();
  }
}

function scan_x() {
  // let sx = my.video.width / 2;
  let sx = (my.sx + 1) % my.video.width;
  my.sx = sx;
  let sy = 0;
  let sw = 1;
  let sh = my.video.height;
  let dx = my.x;
  let dy = 0;
  let dw = 1;
  let dh = height;
  image(my.video, dx, dy, dw, dh, sx, sy, sw, sh);
  my.x = my.x + 1;
  if (my.x > width) {
    my.x = 0;
    flipAction()
  }
}

function scan_y() {
  let sx = 0;
  // let sy = my.video.height / 2;
  let sy = (my.sy + 1) % my.video.height;
  my.sy = sy;
  let sw = my.video.width;
  let sh = 1;
  let dx = 0;
  let dy = my.y;
  let dw = width;
  let dh = 1;
  image(my.video, dx, dy, dw, dh, sx, sy, sw, sh);
  my.y = my.y + 1;
  if (my.y > height) {
    my.y = 0;
    flipAction()
  }
}

function flipAction() {
  my.scanX = !my.scanX;
}

function saveAction() {
  saveCanvas("slit-scan-xy");
}

function video_ready() {
  return my.video.loadedmetadata && my.video.width > 0 && my.video.height > 0;
}

