class Bubble {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.size = 24;
    this.deltaX = 10;
    this.deltaY = 0;
    if (random() < 0.5) {
      this.shape = "rect"
    }
    else {
      this.shape = "circle"
    }
  }

  display() {
    stroke(255);
    fill(255, 0, 150, 50);
    if (this.shape == "rect") {
      rect(this.x, this.y, this.size);
    }
    else {
      circle(this.x, this.y, this.size)
    }
  }

  move() {
    this.x = this.x + this.deltaX;
    this.y = this.y + this.deltaY;
    if (this.x > width || this.x < 0) {
      this.deltaX = this.deltaX * -1.0;
    }
  }
}

