  my.srcImage = loadImage(my.image_name);
}

function setup() {
  my.cnv = createCanvas(390, 600);
  my.cnv.mouseClicked(canvas_mouseClicked);

  init_vars();

  create_ui();

  // show image in original order for a sec before shuffling
  setTimeout(action_shuffle, 1000);
}

function draw() {
