    //       { "c": [ 135, 132, 133, 255 ],
    //          "h": 27, "r": 1, "w": 27, "x": 0, "y": 0
    //       },
  });

  fb_.onChildRemoved(ref, (data) => {
    let key = data.key;
    let val = data.val();
    console.log('dstore_pix_onChild Removed key val=', key, val);
