function fstore_img_download() {
  // console.log('fstore_img_download ');
  let path = next_imagePath(my.count);
  // my.lastDownloadPath = null;
  // ui_log('fstore_img_download next_imagePath ' + path);
  let { storage, ref, getDownloadURL } = fb_.fstore;
  getDownloadURL(ref(storage, path))
    .then((url) => {
      // `url` is the download URL for '1.jpeg'
      // ui_log('fstore_img_download url', url);

      // This can be downloaded directly:
      const xhr = new XMLHttpRequest();
      xhr.responseType = 'blob';
      xhr.onload = (event) => {
        const blob = xhr.response;
        // ui_log('fstore_img_download blob ' + blob);
        renderBlobToCanvas(blob);
        my.lastDownloadPath = path;
        if (my.img_download_log) {
          ui_log('download ' + path);
        }
      };
      xhr.open('GET', url);
      xhr.send();

      // Or inserted into an <img> element
      // let img = createImg(url, 'img test');
      // img.setAttribute('src', url);
    })
    .catch((error) => {
      // Handle any errors
      ui_error('fstore_getDownloadURL error', error);
      my.replayLayer.fill(0);
      my.replayLayer.rect(0, 0, width, height);
    });
}

// https://stackoverflow.com/questions/38004917/how-to-render-a-blob-on-a-canvas-element
// HTMLCanvasElement.prototype.renderImage = function(blob) {

function renderBlobToCanvas(blob) {
  // let elt = my.canvas.elt;
  // let ctx = elt.getContext('2d');
  // let ctx = my.canvas.drawingContext;
  let ctx = my.replayLayer.drawingContext;
  var img = new Image();
  img.onload = function () {
    ctx.drawImage(img, 0, 0, width, height);
    URL.revokeObjectURL(img.src);
  };
  img.src = URL.createObjectURL(blob);
}

function record_state_update(newState) {
  init_counts();
  my.record_preroll = newState;
  let now = millis() / 1000;
  my.recordCountDown.next_secs = now + my.recordCountDown.max;
  if (newState) {
    my.replay = 0;
    my.replayChk.checked(0);
  } else {
    my.recordCountDown.next_secs = 0;
  }
}

function record_state_track() {
  //
  let m = height * 0.08;
  strokeWeight(m);
  if (my.record && !my.record_preroll) {
    stroke(255, 0, 0);
    noFill();
    rect(0, 0, width, height);
  }
  if (!my.record_preroll) {
    return;
  }
  let now = millis() / 1000;
  if (now > my.recordCountDown.next_secs) {
    my.record_preroll = 0;
    return;
  }
  let cnt = my.recordCountDown.next_secs - now;
  cnt = int(cnt + 1);
  let str = cnt + '';
  let h = width * 1.4;
  textSize(h);
  let w = textSize(str);
  // console.log('w', w);
  // let x = (width - w) * 1;
  let x = width * 0.1;
  let y = height * 0.8;
  strokeWeight(m * 0.025);
  stroke(255, 0, 0);
  fill(255, 0, 0);
  text(cnt, x, y);
}

function sound_state_update() {
  if (my.soundEnable) {
    sound_init();
  } else {
    sound_playback_stop();
  }
}

function sound_init() {
  sound_AudioContext_resume();
  // create an audio in
  my.mic = new p5.AudioIn();
  // users must manually enable their browser microphone for recording to work properly!
  my.mic.start();
  // create a sound recorder
  my.recorder = new p5.SoundRecorder();
  // connect the mic to the recorder
  my.recorder.setInput(my.mic);
  // create an empty sound file
  // that we will use to playback the recording
  my.soundFile = new p5.SoundFile();
  my.sound_record_started = 0;

  sound_draw_init();
}

function sound_record_start() {
  console.log('sound_record_start');
  if (my.sound_record_started) {
    return;
  }
  my.sound_record_started = 1;
  // Tell recorder to record to a p5.SoundFile
  // which we will use for playback
  my.recorder.record(my.soundFile);
  // my.amp = new p5.Amplitude();
  // my.amp.setInput(my.soundFile);
}

function sound_AudioContext_resume() {
  let aSoundContext = getAudioContext();
  // console.log('aSoundContext.state', aSoundContext.state);
  if (aSoundContext.state !== 'running') {
    aSoundContext.resume();
  }
}

function sound_record_stop() {
  console.log('sound_record_stop');
  my.sound_record_started = 0;
  // stop recorder, and send the result to soundFile
  my.recorder.stop();
  // Prepare for upload
  my.fstore_sound_upload_started = 1;
  my.fstore_sound_upload_completed = 0;
  my.sound_downFile = null;

  // Give record a sec before asking for blob
  setTimeout(sound_getBlob, 1 * 1000);
}

function sound_playback_start() {
  // console.log('sound_playback_start', my.sound_downFile ? 'soundFile' : '-');
  // skip if soundfile is already playing
  if (my.sound_downFile) {
    return;
  }
  // Wait for upload to complete
  if (
    my.fstore_sound_upload_started && //
    !my.fstore_sound_upload_completed
  ) {
    return;
  }
  // download the sound
  fstore_sound_download();
}

function sound_playback_stop() {
  // console.log('sound_playback_stop', my.sound_downFile ? 'soundFile' : '-');
  if (!my.sound_downFile) {
    return;
  }
  ui_log('sound_playback_stop', my.sound_downFile ? 'soundFile' : '-');
  // stop the sound and reset vars
  my.sound_downFile.stop();
  my.sound_downFile = null;
  my.sound_download_blob = null;
  my.amp = null;
}

function sound_downloaded() {
  my.sound_downFile.play();
  my.sound_downFile.loop();
  if (!my.amp) {
    my.amp = new p5.Amplitude();
  }
  my.amp.setInput(my.sound_downFile);

  sound_draw_init();
}

