function canvas_mousePressed() {
  // console.log('canvas_mousePressed');

  if (keyIsDown(SHIFT)) {
    saveMouseXY();
    my.shiftTracking = 1;
  } else {
    my.mouseTracking = 1;
  }

  if (my.pane1.touchPoint(mouseX, mouseY)) {
    setPane(my.pane1);
  } else if (my.pane0.touchPoint(mouseX, mouseY)) {
    setPane(my.pane0);
  }
  my.pane.mousePressed();
}

function canvas_mouseReleased() {
  // console.log('canvas_mouseReleased');
  if (my.shiftTracking) {
    saveMouseXY();
  }
  my.pane.mouseReleased();
  my.mouseTracking = 0;
  my.shiftTracking = 0;
}

function clearMouseXY() {
  my.mouseXYs = [];
  my.mouseXYindex = 0;
}

function saveMouseXY() {
  let ment = { x: mouseX, y: mouseY };
  my.mouseXYs[my.mouseXYindex] = ment;
  my.mouseXYindex = (my.mouseXYindex + 1) % 2;
}

// !!@ no canvas mouseDragged
// my.canvas.mouseDragged(canvas_mouseDragged);

//
function ui_init() {
  //
  createSpan(my.version.substring(1));
  createSpan().id('id_panX');
  createSpan().id('id_panY');
  createSpan().id('id_zoom');
  createElement('br');
  my.scanFlagChk = createCheckbox('run', my.scanFlag).changed(function () {
    my.scanFlag = this.checked();
    my.animLoop.loop = my.scanFlag;
    my.animLoop.running = my.scanFlag;
    if (my.scanFlag) {
      my.animLoop.restart();
    }
    focusAction();
  });
  my.scanFlagChk.style('display:inline');

  createButton('zero').mousePressed(function () {
    my.pane.pan_init();
  });
  createButton('center').mousePressed(function () {
    my.pane.pan_center();
  });
  createButton('clear').mousePressed(function () {
    clearMouseXY();
  });
  {
    my.zoom_slider = createSlider(1, 32, my.pane.zoomIndex, 0.01).input(function () {
      clearMouseXY();
      my.pane.pan_updateZoom(this.value());
    });
    my.zoom_slider.style('width:500px');
  }
  createElement('br');
  {
    my.refIndex_input = createInput('' + (my.refBox.refIndex + 1))
      .id('id_refIndex')
      .input(function () {
        // console.log('id_refIndex', this.value());
        my.refBox.refIndex = parseFloat(this.value()) - 1;
      });
    my.refIndex_input.size(30);
  }
  createButton('Add').mousePressed(function () {
    addAction();
  });
  createButton('←').mousePressed(function () {
    my.animLoop.restart();
    previousRefAction();
  });
  createButton('→').mousePressed(function () {
    my.animLoop.restart();
    nextRefAction();
  });
  createButton('focus').mousePressed(function () {
    my.animLoop.restart();
    focusAction();
  });
  createButton('update').mousePressed(function () {
    updateAction();
  });
  {
    my.refLabel_input = createInput('' + my.refBox.refLabel)
      .id('id_refLabel')
      .input(function () {
