let score = 0;
let balls = [];
let maxBalls = 7 * 4;
let myColors;
let ncolors = 12; // number of random colors in palette
let colorIndex = 0;
let startupY = 0.5;

// The midi notes of a scale
let notes = [60, 62, 64, 65, 67, 69, 71];
let noteIndex = 0;

// Spawn balls every n frames, 0 for no spawn
let spawnRate = 0; // 60;

// Check for stuck pairs every n frames, 0 for no check
let stuckRate = 60;

let defaultYStart = 0.7;
let bkgAlpha = 10;
let jiggle = 3;
let cmsg = 1;
let hits = [];
let paddle;
let blocks = [];
let blockFlip = 0;

function setup() {
  // createCanvas(windowWidth * 0.5, windowHeight * 0.5); // !!@ Chaos
  createCanvas(windowWidth * 0.95, windowHeight * 0.95);

  init_myColors();

  paddle = new Block();

  // init the first ball to drop from left edge
  let b1 = new Ball();
  b1.xdir = 0;
  b1.ydir = b1.w;
  b1.x = 0;
  b1.y = 0;
  b1.y = height * defaultYStart; // !!@ Chaos on start
  balls.push(b1);
}

function draw() {
  //
  // background(220, bkgAlpha);

  draw_balls();

  draw_blocks();

  paddle.x = mouseX - paddle.w / 2;
  paddle.render();

  check_collisions();

  check_block_collisions();

  // Score
  fill("blue");
  textSize(24);
  // text("Score:" + score, 10, 25);
  text("nballs:" + balls.length, 10, 25);

  check_spawn();
}

function draw_balls() {
  for (let i = 0; i < balls.length; i++) {
    let b = balls[i];
    b.move();

    if (b.isHit(paddle)) {
      score++;
      b.bounceY();
      b.playNote();
      addBall();
    }
  }
}

function addBall() {
  trimsBalls();
  let b = new Ball();
  balls.push(b);
  return b;
}

function draw_blocks() {
  for (let i1 = 0; i1 < blocks.length; i1++) {
    let o1 = blocks[i1];
    o1.render();
  }
}

// Remove a ball if number of balls exceeed maxBalls
function trimsBalls() {
  if (balls.length >= maxBalls) {
    balls.splice(0, 1);
  }
}

function check_spawn() {
  if (spawnRate && frameCount % spawnRate == 0) {
    let b = addBall();
    b.x = floor(random(0, width));
    b.y = floor(random(0, height));
  }
}

function mousePressed() {
  let nblocks = [];
  for (let i1 = 0; i1 < blocks.length; i1++) {
