let my = {
  version: 2, // update to verify change on mobile
  width: 393, // canvas width
  height: 600, // canvas height
  vwidth: 240, // Aspect ratio of video capture
  vheight: 320,
  alpha: 255,
  rotate: 1,
  angle: 0,
  angleDelta: 10,
  scanX: 1,
  x: 0,
  y: 0,
};

function setup() {
  createCanvas(my.width, my.height);
  pixelDensity(1);
  angleMode(DEGREES); 

  my.video = createCapture(VIDEO);
  my.video.size(my.vwidth, my.vheight);
  my.video.hide();

  my.saveBtn = createButton("Save");
  my.saveBtn.mousePressed(saveAction);

  my.flipBtn = createButton("Flip");
  my.flipBtn.mousePressed(flipAction);

  my.rotateBtn = createButton("Rotate");
  my.rotateBtn.mousePressed(rotateAction);

}

function draw() {

  translate(width/2, height/2)
  rotate(my.angle)
  tint(255,my.alpha)

  if (my.scanX) {
    scan_x();
  }  
  else {
    scan_y();
  }
}

function scan_x() {
  let sw = 1;
  let sx = my.video.width / 2;
  let sh = my.video.height;
  let sy = sh / 4;
  // sh = sy * 2;
  let dx = my.x;
  let dy = 0;
  let dw = 1;
  let dh = height;
  image(my.video, dx, dy, dw, dh, sx, sy, sw, sh);
  my.x = my.x + 1;
  if (my.x > width/2) {
    my.x = -width/2;
    cycleAction()
  }
}

function scan_y() {
  let sx = 0;
  let sy = my.video.height / 2;
  let sw = my.video.width;
  let sh = 1;
  let dx = 0;
  let dy = my.y;
  let dw = width;
  let dh = 1;
  image(my.video, dx, dy, dw, dh, sx, sy, sw, sh);
  my.y = my.y + 1;
  if (my.y > height/2) {
    my.y = 0;
    cycleAction()
  }
}

function rotateAction() {
  my.rotate = ! my.rotate;
  if (! my.rotate) {
    my.angle = 0;
  }
}

function cycleAction() {
  if (my.rotate) {
    my.angle = (my.angle + my.angleDelta) % 360;
    if (my.angle == 0) {
      flipAction()
    }
  }
}

function flipAction() {
  my.scanX = !my.scanX;
  my.x = 0;
  my.y = 0;
  my.angle = 0;
}

function saveAction() {
  saveCanvas("slit-scan-rotate");
}

