html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

function draw_random() {
  background(51);
  loadPixels();
  let w1 = 0;
  let w2 = width/ 3;
  for (let y = 0; y < height; y++) {
    for (let x = w1; x < w2; x++) {
      var index = (x + y * width) * 4;
      pixels[index + 0] = x;
      pixels[index + 1] = random(255);
      pixels[index + 2] = y;
      pixels[index + 3] = 255;
    }
  }
  updatePixels();
}

var video;
var vScale = 16;

function setup_bright() {
  video = createCapture(VIDEO);
  video.size(width / vScale, height / vScale);
}

function draw_bright() {
  // background(51);
  if (!video.loadedmetadata) return;
  video.loadPixels();
  let w1 = floor(video.width/3);
  let w2 = floor(w1 * 2);
  for (var y = 0; y < video.height; y++) {
    for (var x = w1; x < w2; x++) {
      var index = (video.width - x + 1 + y * video.width) * 4;
      var r = video.pixels[index + 0];
      var g = video.pixels[index + 1];
      var b = video.pixels[index + 2];
      var bright = (r + g + b) / 3;
      var w = map(bright, 0, 255, 0, vScale);
      noStroke();
      fill(255);
      rectMode(CENTER);
      rect(x * vScale, y * vScale, w, w);
    }
  }
}

let particles = [];
let slider;

function setup_paint() {
  for (var i = 0; i < 200; i++) {
    particles[i] = new Particle(random(width), random(height));
  }
  slider = createSlider(0, 255, 127);
  background(0);
}

function draw_paint() {
  video.loadPixels();
  for (let i = 0; i < particles.length; i++) {
    particles[i].update();
    particles[i].show();
  }
}

class Particle {
  constructor(x, y) {
    this.w1 = floor(width/3)*2;
    this.w2 = width
    this.x = constrain(x, this.w1, this.w2);
    this.y = constrain(y, 0, height);
    this.r = random(4, 32);
  }

  update() {
    this.x += random(-10, 10);
    this.y += random(-10, 10);

    this.x = constrain(this.x, this.w1, this.w2);
    this.y = constrain(this.y, 0, height);
  }

  show() {
    noStroke();
    let px = floor(this.x / vScale);
    let py = floor(this.y / vScale);
    let col = video.get(px, py);
    //console.log(col);
    fill(col[0], col[1], col[2], slider.value());
    ellipse(this.x, this.y, this.r, this.r);
  }
}

let upScale = 2;
let drawers = [draw_random, draw_bright, draw_paint];
let drawerIndex = 0;
let timeStart;
let timeDelay;
let timeDelays = [ 0.5, 0.5, 0.5, 4, 4, 4];
let timeIndex = 0;

function setup() {
  createCanvas(320 * upScale, 240 * upScale);
  pixelDensity(1);
  setup_bright();
  setup_paint();
  timeStart = millis() / 1000;
  timeDelay = 2;
}

function draw() {
  // draw_random()
  // draw_bright();
  // draw_paint();
  let func = drawers[drawerIndex]
  func();
  let now = millis() / 1000;
  if (now > timeStart + timeDelay) {
    drawerIndex = (drawerIndex+1) % drawers.length;
    timeStart = now;
    timeIndex = (timeIndex + 1) % timeDelays.length;
    timeDelay = timeDelays[timeIndex];
  }
  // if (frameCount % 60 == 0) {
  //   drawerIndex = (drawerIndex+1) % drawers.length;
  // }
}

